import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';

export class News extends Component {

    constructor() {
        super();
        // console.log("hellow i am constructor..😍");

        this.state = {
            articles: [],
            loading: false,
            page: 1
        }
    }

    async componentDidMount() {
        let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=14bfa21dd6fc493e9dd82c9a4f61158f&page=1&pageSize=${this.props.pageSize}`;
        this.setState({loading:true});
        let data = await fetch(url);
        let parseData = await data.json();
        console.log(data);
        this.setState({ articles: parseData.articles,
            totalArticles: parseData.totalResults,
            loading: false
         })
    }

    handlePrevClick = async () => {
        // console.log("prev");
        let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=14bfa21dd6fc493e9dd82c9a4f61158f&page=${this.state.page - 1} &pageSize=${this.props.pageSize}`;
        let data = await fetch(url);
        this.setState({loading:true});
        let parseData = await data.json();
        this.setState({loading:false});
        this.setState({
            page: this.state.page - 1,
            articles: parseData.articles,
        })
    }

    handleNextClick = async () => {
            // console.log("next");
            if(this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize))
            {

            }
            else
            {

                let url = `https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=14bfa21dd6fc493e9dd82c9a4f61158f&page=${this.state.page + 1} &pageSize=${this.props.pageSize}`;
                let data = await fetch(url);
                this.setState({loading:true});
                let parseData = await data.json();
                this.setState({loading:false});
                this.setState({
                    page: this.state.page + 1,
                    articles: parseData.articles
                })
            }
    }

    render() {
        return (
            <div className='mx-56 mt-9'>
                <div>
                    <h1 className='font-bold text-2xl text-center'>NewsMonkey - Top Headlines</h1>
                    {/* this is the logic to run the spinner whenever the data is loaded */}
                   {this.state.loading && <Spinner />}
                </div>
                <div className='grid grid-cols-3 my-8 gap-3'>
                    {this.state.articles.map((element) => {
                        return <div key={element.url}>
                            {/* now untill here we did a lot of things, so lets try this out..😁
                             if i click on read more.. then the whole news should displayed on new window..
                             */}
                            {/* now here we set some conditions --> if let suppose some of the fields such as auther or imageUrl is null then it will throw an error. So, to prevent that we will use ternary operator, as follows */}
                            <NewsItem title={element.title ? element.title.slice(0, 66) : ""} description={element.description ? element.description.slice(0, 124) : ""} Imgurl={element.urlToImage} newsUrl={element.url} />
                        </div>
                    })}
                </div>

                {/* now as we have added buttons we need to define their logic, so here also we will set the logic using onclick events 
                also we need set conditions to our previous buttons cause page no. cannot go back to page no. 1
                so use disabled method
                */}
                <div className="container flex justify-between">
                    <div>
                        <button disabled={this.state.page <= 1} className='bg-gray-800 text-white text-xl rounded-md px-2 font-medium py-0.5' onClick={this.handlePrevClick}>&larr; Previous</button>
                    </div>
                    <div className='mt-7 mb-2'>
                        <span className='font-medium'>page </span> {this.state.page}
                    </div>
                    {/* <Spinner/> */}
                    <div>
                        <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize)} className='bg-gray-800 text-white text-xl rounded-md px-2 font-medium py-0.5' onClick={this.handleNextClick}>Next &rarr;</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default News
