import React, { Component } from 'react'
import { Link } from 'react-router-dom';


export class NewsItem extends Component {

    render() {
        // here by using this properties we can set the title and description of individual news.
        let {title, description, Imgurl, newsUrl, author, date} = this.props;
        return (
            <div className="card">
                <img src={Imgurl} className="card-img-top h-72" alt="" />
                <div className="card-body outline outline-gray-200">
                        <h5 className="card-title"><strong>{title}...</strong></h5>
                        <p className="card-text mb-1.5">{description}</p>
                        {/* <a href={newsUrl} target="_blank" className="bg-blue-600 text-white text-lg text-center px-3 py-1 font-medium rounded-md mt-3">Read more..</a> */}
                        <p className="card-text mb-2 text-sm font-light">By {author} on {date}</p>
                        <a rel="noreferrer" href={newsUrl} target="_blank" className='bg-black text-white text-lg text-center px-3 py-1.5 mt-8 font-medium rounded-md'>Read more..</a>
                    </div>
            </div>
        )
    }
}

export default NewsItem
