import React, { Component } from 'react'

export class NewsItem extends Component {
    render() {
        // here by using this properties we can set the title and description of individual news.
        let {title, description, Imgurl} = this.props;
        return (
            <div className="cardpy-3" style={{width:'18rem'}}>
                <img src={Imgurl} className="card-img-top h-72" alt="" />
                <div className="card-body outline outline-gray-200">
                        <h5 className="card-title">{title}</h5>
                        <p className="card-text">{description}</p>
                        {/* <a href="/" className="btn btn-primary">Go somewhere</a> */}
                        <button className='bg-blue-600 text-white text-lg text-center px-3 py-1.5 font-medium rounded-md'>Read more..</button>
                    </div>
            </div>
        )
    }s
}

export default NewsItem
